import axios from "axios"
import { useEffect, useState } from "react"
import {useAsyncValue, useNavigate } from "react-router-dom"
import { useContext } from "react"
import { Context } from "../../Context/AuthContext"
import { FaPlus } from 'react-icons/fa'
import styles  from '../styling/panel.module.css'

function UserPanel(){

const [loading, setLoading] = useState(false)
const [inputs, setInputs] = useState([])
const [albumImage, setAlbumImage] = useState()
const [tracks, setTracks] = useState({})
const [toggle, setToggle] = useState(false)
const {authenticated} = useContext(Context)    
const [data, setData] = useState({})
const [albumName, setAlbumName] = useState("")
const [secureUrl, setSecureUrl] = useState()
const navigate = useNavigate()
const userId = localStorage.getItem("user_id")
let newStateArray = inputs.slice()

useEffect(() => {
axios.get(`http://localhost:3001/api/users/${userId}`, {
    headers: { 'x-access-token': localStorage.getItem('token')}
})
.then((response) => {
setData(response.data)
})

.catch((error) => {
    if(error.response.status === 401){
        navigate("/login")
        
    }else if(error.response.data.message === 'jwt expired'){
        navigate("/logout")
    }
})
},[])
let formData = new FormData()
formData.append("upload_preset", "b04ex0je")
function handleSubmit(e){
    e.preventDefault();
    console.log(inputs)
    setLoading(true)
    for(let i = 0; i < inputs.length; i++){
        let file = tracks[`track${i}`].file
        console.log(file)
    formData.append(`file`, file)
    axios.post("https://api.cloudinary.com/v1_1/doaz7klln/video/upload", formData, {
        headers:{}
    }
    )
    .then((response) =>{
     console.log(response)
     setSecureUrl({...secureUrl, [`track${i}`]:{secure_url: response.secure_url}})
     console.log(secureUrl)

    })

    }
    formData.append("file", albumImage)
    axios.post("https://api.cloudinary.com/v1_1/doaz7klln/image/upload", formData, {
        headers:{}
    }).then((response) => setLoading(false))

    for (var [key, value] of formData.entries()) {
        console.log([key],value); 
    }

}
function handleClick(){
    setToggle(true)
    if(toggle === true){
        setToggle(false)
    }
    newStateArray = [1]    
    }
function addTrack(e){
    let obj = tracks
    obj[e.target.name].name = e.target.value
    setTracks(obj)
    console.log(tracks)

}
function setTrackFile(e){
  
    let obj = tracks
    obj[e.target.name].file = e.target.files[0];
    setTracks(obj)

}
function addInput(e){
    setTracks({...tracks, [`track${inputs.length}`]:{name:'',file:''}})
   newStateArray.push(inputs.length+1)
   setInputs(newStateArray)
   console.log(tracks)
   console.log(albumImage)

}
return(
    <div className={styles.panelContainer}>
{!authenticated &&
        <div>
        <p>401 unauthorized</p>
        </div> 
}
{authenticated &&
<div>
{loading && 
    <div className={styles.loading}>
        <h1>Sending data to server, please wait...</h1>
        <img src={require('../styling/loading.gif')} />
    </div>
}{!loading &&
<div>
<h1>Hello, {data.name}</h1>
<div className={styles.formDiv}>
    <h1 className={styles.ddclick} onClick={(() => {handleClick()})}><FaPlus/> Create a new album</h1>
    <form className={toggle ? styles.dropdown_toggle : styles.dropdown} onSubmit={((e) => handleSubmit(e))}>
        <div className={styles.inputField}>
        <label htmlFor="name">Album name</label>
        <br/>
        <input type="text" value={albumName} onChange={(e) => setAlbumName(e.target.value)}></input>
        </div>
        <div className={styles.inputField}>
        <label htmlFor="cover">Album image cover</label>
        <br></br>

        <input type="file" required id="fileInput" onChange={((e) => {setAlbumImage(e.target.files[0])})} ></input>
        <br></br>

        {inputs.map((t,i) => {
            return(
            <div className={styles.inputContainer}>
                  <label htmlFor="name"> Track {i+1} name:
                  <br></br>
                  </label>
                  <input type="text" required name={`track${i}`} onChange={((e) => {addTrack(e,i)})}></input>
                  <div className={styles.inputField}>
                  <label htmlFor="name"> Track {i+1} file:
                  </label>
                  <br></br>
                  <input type="file" id="fileInput" required name={`track${i}`} onChange={((e) => { setTrackFile(e) })} accept=".mp3, .wav, .flac, .ogg"></input>
                  </div>
                  <br></br>

                  </div>
                  
                  )
        })
    }        <br></br>
    <div className={styles.inputField}>
    <h3 className={styles.addTrack} onClick={((e) => {addInput()})}> <FaPlus/> Add track</h3>
    </div>
            <div className={styles.inputField}>
    <input type="submit"></input>
    </div>
        </div>    
        </form>
        <hr></hr>

    </div>
    </div>
    
}
</div>
}
    </div>
    )

}
export default UserPanel
