import axios from 'axios';
import { useState, useEffect } from 'react'
import styles from '../styling/form.module.css'
import { Link } from 'react-router-dom';


function Login(){
    const [loading, setLoading] = useState(true)
    const [authenticated, setAuthenticated] = useState(false)
    const [error, setError] = useState(false)
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState() 
    const [message, setMessage] = useState("")

    const instance = axios.create({
        baseURL: 'https://localhost:3001/api/users/'
    });

    useEffect(()=> {
        const token = localStorage.getItem('token')
        if(token){
            instance.defaults.headers["Authorization"] = `Bearer ${JSON.parse(token)}`;
            setAuthenticated(true)
            setLoading(false)
        }
    },[])

    function handleSubmit(e){
        e.preventDefault();
        axios.post('http://localhost:3001/api/users/login/', {
            
            email: email,
            password: password
        })
        .then((response) => {
            const {token} = response.data
            setError(false)
            setMessage(response.data.message)
            setAuthenticated(true)
            localStorage.setItem("token", JSON.stringify(token));
            instance.defaults.headers["Authorization"] = `Bearer ${token}`;

        
        }).catch((error) => {
            if(error.response){
                setError(true)
                setMessage(error.response.data.message)
            }
        })
    }
    return(
        <div className={styles.background}>
<div className={styles.form}>
    <form onSubmit={handleSubmit}>
    <div className={styles.inputDiv}>
        <label htmlFor='name'>
            Enter your e-mail
        </label>
        <input type="text" value={email} onChange={(e) => {setEmail(e.target.value)}}/>
        </div>
        <div className={styles.inputDiv}>
        <label htmlFor="password">
            Enter your password
        </label>
        <input type="password" value={password} onChange={(e) => {setPassword(e.target.value)}}>
        </input>
        <p>Don't have an account yet? <Link to="/signup/"> <span>Sign up</span> </Link></p>
        </div>
        <button type='submit'>Submit</button>
        <p className={styles[error ? 'error' : 'success']}>{message}</p>

    </form>
</div>
</div>
    )
    }

export default Login
