import axios from 'axios';
import { useState } from 'react'
import styles from '../styling/form.module.css'
import { Link } from 'react-router-dom';
import { useContext } from 'react';
import { Context } from '../../Context/AuthContext';


function Login(){
    const {authenticated, error, message, handleSubmit} = useContext(Context)
  
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState() 
    return(
        <div className={styles.background}>
<div className={styles.form}>
    <form onSubmit={handleSubmit}>
    <div className={styles.inputDiv}>
        <label htmlFor='name'>
            Enter your e-mail
        </label>
        <input type="text" value={email} onChange={(e) => {setEmail(e.target.value)}}/>
        </div>
        <div className={styles.inputDiv}>
        <label htmlFor="password">
            Enter your password
        </label>
        <input type="password" value={password} onChange={(e) => {setPassword(e.target.value)}}>
        </input>
        <p>Don't have an account yet? <Link to="/signup/"> <span>Sign up</span> </Link></p>
        </div>
        <button type='submit'>Submit</button>
        <p className={styles[error ? 'error' : 'success']}>{message}</p>

    </form>
</div>
</div>
    )
}

export default Login
